mapper.select:
SELECT

FROM t_cms_article a

WHERE a.id = #{id}

SELECT
FROM t_cms_article a
a.status = #{status}
AND a.title LIKE '%'||#{title}||'%' CONCAT('%', #{title}, '%')
AND a.posid LIKE '%'||#{posid}||'%' CONCAT('%', #{posid}, '%')
AND (a.category_id = #{category.id}
or c.parent_ids like '%'||#{category.id}||'%' CONCAT('%,', #{category.id}, ',%') )
AND c.device_config LIKE '%'||#{category.deviceConfig}||'%' CONCAT('%,', #{category.deviceConfig}, ',%')
AND a.image = #{image}
AND a.create_user = #{createUser}
ORDER BY ${entityPage.orderBy}
ORDER BY a.weight DESC, a.update_time DESC

SELECT

FROM t_cms_article a


	a.status = #{STATUS_NORMAL}



		ORDER BY ${entityPage.orderBy}


		ORDER BY a.weight DESC, a.update_time DESC

mapper.update:
UPDATE t_cms_article SET
	status = #{STATUS_DELETE}
WHERE id = #{id}

mapper.select:
