from t_cms_article a where

	id in (${id});

mapper.update:
update t_cms_article set
	 hits = hits+1
 WHERE id = #{id}

mapper.select:
select max(c.id) AS "id", max(c.name) AS "name", max(cp.id) AS "parent.id", max(cp.name) AS "parent.name", count(*) AS "cnt", sum(a.hits) AS "hits", max(a.update_time) as "updateTime", max(o.id) AS "organId", max(o.name) AS "organName" FROM t_cms_article a RIGHT JOIN t_cms_category c ON c.id = a.category_id JOIN t_cms_category cp ON c.parent_id = cp.id JOIN t_sys_organ o ON o.id = c.organ_id
a.status = #{STATUS_NORMAL} AND c.site_id = #{site.id}
AND (c.organ_id = #{organId} OR o.parent_ids like '%'||#{organId}||'%' CONCAT('%', #{organId}, '%'))
AND a.update_time >= #{beginDate}
AND a.update_time <= #{endDate}
AND (c.id = #{id} OR c.parent_ids LIKE '%'||#{id}||'%' CONCAT('%', #{id}, '%'))
group by cp.sort, cp.id, c.sort, c.id order by cp.sort, cp.id, c.sort, c.id
