mapper.select:
SELECT 

FROM t_cms_comment a

WHERE a.id = #{id}

SELECT
FROM t_cms_comment a
a.status = #{status}
AND a.title LIKE '%'||#{title}||'%' CONCAT('%', #{title}, '%')
ORDER BY ${entityPage.orderBy}
ORDER BY a.create_time DESC

SELECT 

FROM t_cms_comment a


	a.status = #{STATUS_NORMAL}



		ORDER BY ${entityPage.orderBy}


		ORDER BY a.create_time DESC

mapper.update:
UPDATE t_cms_comment SET
	status = #{STATUS_DELETE}
WHERE id = #{id}
