mapper.select:
SELECT 

FROM t_cms_site a

WHERE a.id = #{id}

SELECT

FROM t_cms_site a

WHERE a.code = #{code}
	AND a.status = #{STATUS_NORMAL}

SELECT
FROM t_cms_site a
a.status = #{status}
AND a.name LIKE '%'||#{name}||'%' CONCAT('%', #{name}, '%')
ORDER BY ${entityPage.orderBy}
ORDER BY a.update_time DESC

SELECT 

FROM t_cms_site a


	a.status = #{STATUS_NORMAL}



		ORDER BY ${entityPage.orderBy}


		ORDER BY a.update_time DESC

mapper.update:
UPDATE t_cms_site SET
	status = #{STATUS_DELETE}
WHERE id = #{id}
